// RSS parsing code for Node.js environment (without DOMParser)

// Get HTTP request response data
let rssData;
const inputData = items[0];

console.log('========== Input Data Check ==========');
console.log('Input data type:', typeof inputData);
console.log('Input data properties:', Object.keys(inputData));

// Try multiple possible data paths
if (inputData.body) {
  rssData = inputData.body;
} else if (inputData.json && inputData.json.data) {
  rssData = inputData.json.data;
} else if (inputData.json && typeof inputData.json === 'string') {
  rssData = inputData.json;
} else if (inputData.data) {
  rssData = inputData.data;
} else if (typeof inputData === 'string') {
  rssData = inputData;
} else {
  console.log('Full input data:', JSON.stringify(inputData, null, 2));
  throw new Error('Cannot find RSS data, please check HTTP request node response format');
}

console.log('RSS data type:', typeof rssData);
console.log('RSS data length:', rssData ? rssData.length : 'undefined');
console.log('RSS content preview:', rssData ? rssData.substring(0, 300) : 'no data');

// Ensure rssData is a string
if (typeof rssData !== 'string') {
  if (rssData && typeof rssData === 'object') {
    rssData = JSON.stringify(rssData);
  } else {
    throw new Error('RSS data is not a valid string format');
  }
}

// Parse RSS XML using regex (Node.js compatible)
let articles = [];

try {
  console.log('========== Starting RSS Parsing ==========');

  // Check if valid XML
  if (!rssData.includes('<rss') && !rssData.includes('<feed')) {
    throw new Error('Not a valid RSS or Atom format');
  }

  // Extract all <item> tags using regex
  const itemRegex = /<item[^>]*>([\s\S]*?)<\/item>/gi;
  const itemMatches = rssData.match(itemRegex);

  if (!itemMatches || itemMatches.length === 0) {
    throw new Error('No item elements found in RSS');
  }

  console.log('Number of items found:', itemMatches.length);

  // Parse each item
  itemMatches.forEach((itemXml, index) => {
    try {
      // Extract title
      const titleMatch = itemXml.match(/<title[^>]*><!\[CDATA\[(.*?)\]\]><\/title>|<title[^>]*>(.*?)<\/title>/i);
      const title = titleMatch ? (titleMatch[1] || titleMatch[2] || '').trim() : '';

      // Extract description
      const descMatch = itemXml.match(/<description[^>]*><!\[CDATA\[(.*?)\]\]><\/description>|<description[^>]*>(.*?)<\/description>/i);
      let description = descMatch ? (descMatch[1] || descMatch[2] || '').trim() : '';

      // Clean HTML tags
      description = description.replace(/<[^>]*>/g, '').trim();

      // Extract link
      const linkMatch = itemXml.match(/<link[^>]*>(.*?)<\/link>/i);
      const link = linkMatch ? linkMatch[1].trim() : '';

      // Extract publication date
      const pubDateMatch = itemXml.match(/<pubDate[^>]*>(.*?)<\/pubDate>/i);
      let pubDate = new Date();
      if (pubDateMatch) {
        const dateString = pubDateMatch[1].trim();
        pubDate = new Date(dateString);
        if (isNaN(pubDate.getTime())) {
          console.warn('Cannot parse date:', dateString);
          pubDate = new Date();
        }
      }

      // Extract category
      const categoryMatch = itemXml.match(/<category[^>]*>(.*?)<\/category>/i);
      const category = categoryMatch ? categoryMatch[1].trim() : 'Finance News';

      // Calculate news importance score
      let importanceScore = 0;

      // Important keywords scoring (adjusted for Financial Times content)
      const importantKeywords = [
        'AI', 'artificial intelligence', 'machine learning', 'deep learning', 'ChatGPT', 'OpenAI',
        'chip', 'semiconductor', 'TSMC', 'Taiwan Semiconductor', 'MediaTek',
        'Apple', 'iPhone', 'iPad', 'Mac',
        'Microsoft', 'Windows', 'Azure',
        'Google', 'Android', 'Chrome',
        'NVIDIA', 'AMD', 'Intel',
        'Tesla', 'electric vehicle', 'EV', 'autonomous driving',
        '5G', '6G', 'IoT', 'cloud', 'blockchain', 'cryptocurrency',
        'metaverse', 'VR', 'AR', 'virtual reality', 'augmented reality',
        'quantum', 'quantum computing', 'quantum computing',
        'space', 'SpaceX', 'satellite', 'rocket',
        'biotech', 'gene', 'DNA', 'medical technology',
        'startup', 'funding', 'investment', 'IPO',
        'ASUS', 'Acer', 'Foxconn',
        'cybersecurity', 'hacker', 'network security', 'privacy',
        'open source', 'Linux'
      ];

      const fullText = title + ' ' + description;
      importantKeywords.forEach(keyword => {
        if (fullText.includes(keyword)) {
          importanceScore += 10;
          // Extra points if keyword appears in title
          if (title.includes(keyword)) {
            importanceScore += 5;
          }
        }
      });

      // Newer news gets higher scores
      const hoursOld = (new Date() - pubDate) / (1000 * 60 * 60);
      if (hoursOld < 6) importanceScore += 30;
      else if (hoursOld < 24) importanceScore += 20;
      else if (hoursOld < 48) importanceScore += 10;

      // Moderate title length gets points
      if (title.length > 10 && title.length < 100) {
        importanceScore += 5;
      }

      // Detailed description gets points
      if (description.length > 50) {
        importanceScore += 5;
      }

      // Only add valid articles
      if (title && link) {
        articles.push({
          title: title,
          description: description || 'No description available',
          link: link,
          pubDate: pubDate.toISOString(),
          importanceScore: importanceScore,
          category: category
        });

        console.log(`Parsed article ${index + 1}:`, {
          title: title.substring(0, 50) + '...',
          score: importanceScore,
          hasLink: !!link
        });
      } else {
        console.log(`Skipped invalid article ${index + 1}:`, { title, hasLink: !!link });
      }

    } catch (itemError) {
      console.error(`Error parsing item ${index + 1}:`, itemError.message);
      // Continue processing next item
    }
  });

  console.log('Successfully parsed articles count:', articles.length);

} catch (error) {
  console.error('RSS parsing error:', error);
  console.error('Error details:', error.stack);
  throw new Error('RSS parsing failed: ' + error.message);
}

// Filter out invalid articles
const validArticles = articles.filter(article => {
  const hasValidTitle = article.title && article.title.trim() !== '';
  const hasValidLink = article.link && article.link.trim() !== '';

  return hasValidTitle && hasValidLink;
});

console.log('Valid articles after filtering:', validArticles.length);

if (validArticles.length === 0) {
  throw new Error('No valid news articles found, please check RSS source');
}

// Sort by importance score and select top 3
const topArticles = validArticles
  .sort((a, b) => b.importanceScore - a.importanceScore)
  .slice(0, Math.min(3, validArticles.length));

console.log('========== Selected Important News ==========');
topArticles.forEach((article, index) => {
  console.log(`Rank ${index + 1}:`);
  console.log('Title:', article.title);
  console.log('Importance Score:', article.importanceScore);
  console.log('Publication Date:', article.pubDate);
  console.log('Category:', article.category);
  console.log('Link:', article.link);
  console.log('Description Preview:', article.description.substring(0, 100) + '...');
  console.log('---');
});

// Output data
const outputData = {
  selectedArticles: topArticles,
  totalArticles: validArticles.length,
  processedAt: new Date().toISOString(),
  source: 'Financial Times',
  rssUrl: 'https://www.ft.com/?format=rss'
};

console.log('========== Output Data Summary ==========');
console.log('Selected articles count:', outputData.selectedArticles.length);
console.log('Total articles count:', outputData.totalArticles);
console.log('Processing time:', outputData.processedAt);

return [{ json: outputData }];

