// Fixed version: RSS parsing result check node
const data = items[0].json;

console.log('========== RSS Parsing Result Check ==========');
console.log('Input data structure:', Object.keys(data));
console.log('Selected articles count:', data.selectedArticles ? data.selectedArticles.length : 0);
console.log('Total articles count:', data.totalArticles || 0);
console.log('Data source:', data.source || 'unknown');

// Check RSS parsing results
if (!data.selectedArticles || !Array.isArray(data.selectedArticles)) {
  throw new Error('RSS parsing failed: No selected articles found');
}

if (data.selectedArticles.length === 0) {
  throw new Error('RSS parsing failed: No articles selected');
}

// Check completeness of each article
let validArticleCount = 0;
data.selectedArticles.forEach((article, index) => {
  const hasTitle = article.title && article.title.trim() !== '';
  const hasLink = article.link && article.link.trim() !== '';
  const hasDescription = article.description && article.description.trim() !== '';

  console.log(`Article ${index + 1}:`);
  console.log('- Title:', hasTitle ? 'âœ“' : 'âœ—', article.title?.substring(0, 50) + '...');
  console.log('- Link:', hasLink ? 'âœ“' : 'âœ—', article.link?.substring(0, 50) + '...');
  console.log('- Description:', hasDescription ? 'âœ“' : 'âœ—', article.description?.substring(0, 50) + '...');
  console.log('- Importance Score:', article.importanceScore || 0);

  if (hasTitle && hasLink) {
    validArticleCount++;
  }
});

console.log('Valid articles count:', validArticleCount);

if (validArticleCount === 0) {
  throw new Error('All selected articles are missing required information (title or link)');
}

console.log('âœ… RSS parsing result check passed');
console.log('========================================');

// Pass data directly to next node
return items;
