// Process Webhook image data
const inputData = $input.first();
console.log('=== Webhook Input Data Analysis ===');
console.log('Input data structure:', {
  hasBinary: !!inputData.binary,
  hasJson: !!inputData.json,
  binaryKeys: inputData.binary ? Object.keys(inputData.binary) : [],
  jsonKeys: inputData.json ? Object.keys(inputData.json) : []
});

// Method 1: Check for standard binary format
if (inputData.binary && inputData.binary.image) {
  console.log('‚úÖ Found standard binary image data');
  return {
    json: {
      success: true,
      imageProcessed: true,
      source: 'binary_direct'
    },
    binary: inputData.binary
  };
}

// Method 2: Process base64 image data in JSON
if (inputData.json && inputData.json.image) {
  let imageData = inputData.json.image;
  let mimeType = 'image/png';

  console.log('‚úÖ Found image data in JSON, length:', imageData.length);

  // Handle complete data URL format
  if (imageData.startsWith('data:')) {
    const [header, base64Data] = imageData.split(',');
    const mimeMatch = header.match(/:(.*?);/);
    mimeType = mimeMatch ? mimeMatch[1] : 'image/png';
    imageData = base64Data;
    console.log('Parsed data URL, MIME type:', mimeType);
  }

  try {
    // Convert to Buffer (mimicking Form Trigger format)
    const buffer = Buffer.from(imageData, 'base64');
    console.log('‚úÖ Successfully converted to Buffer, size:', buffer.length, 'bytes');

    return {
      json: {
        success: true,
        imageProcessed: true,
        source: 'json_base64',
        mimeType: mimeType
      },
      binary: {
        image: {
          data: buffer,
          mimeType: mimeType,
          fileName: 'uploaded_image.png'
        }
      }
    };
  } catch (error) {
    console.error('‚ùå base64 conversion failed:', error.message);
    return {
      json: {
        error: 'base64 data conversion failed: ' + error.message,
        success: false,
        source: 'conversion_error'
      }
    };
  }
}

// Method 3: Check multipart form data format
if (inputData.json && inputData.json.files) {
  console.log('Checking multipart files');
  const files = inputData.json.files;
  const file = files.image || files[0];

  if (file && file.content) {
    try {
      const buffer = Buffer.from(file.content, 'base64');
      return {
        json: {
          success: true,
          imageProcessed: true,
          source: 'multipart'
        },
        binary: {
          image: {
            data: buffer,
            mimeType: file.mimetype || 'image/png',
            fileName: file.filename || 'uploaded_image.png'
          }
        }
      };
    } catch (error) {
      console.error('‚ùå multipart processing failed:', error.message);
    }
  }
}

// No image data found
console.log('‚ùå No image data found');
console.log('Available keys:', {
  json: inputData.json ? Object.keys(inputData.json) : 'no json',
  binary: inputData.binary ? Object.keys(inputData.binary) : 'no binary'
});

return {
  json: {
    error: 'No image data found, please check upload format',
    success: false,
    availableKeys: {
      json: inputData.json ? Object.keys(inputData.json) : [],
      binary: inputData.binary ? Object.keys(inputData.binary) : []
    },
    source: 'no_image_found'
  }
};

// Based on successful logic
const inputData = $input.first().json;
console.log('=== AI Agent Output Analysis ===');
console.log('Output structure:', JSON.stringify(inputData, null, 2));

// Check for inline image data (Gemini format)
let imageData = null;
let mimeType = null;
let foundLocation = null;

// Check path 1: inlineData in output array
if (inputData.output && Array.isArray(inputData.output)) {
  console.log('Checking output array, length:', inputData.output.length);

  for (let i = 0; i < inputData.output.length; i++) {
    const item = inputData.output[i];
    console.log(`Output[${i}]:`, Object.keys(item));

    if (item.inlineData && item.inlineData.data) {
      imageData = item.inlineData.data;
      mimeType = item.inlineData.mimeType || 'image/png';
      foundLocation = `output[${i}].inlineData`;
      console.log('‚úÖ Found inline image data in output, MIME type:', mimeType);
      break;
    }
  }
}

// Check path 2: candidates structure (another Gemini format)
if (!imageData && inputData.candidates && Array.isArray(inputData.candidates)) {
  console.log('Checking candidates array, length:', inputData.candidates.length);

  for (let i = 0; i < inputData.candidates.length; i++) {
    const candidate = inputData.candidates[i];
    if (candidate.content && candidate.content.parts) {
      for (let j = 0; j < candidate.content.parts.length; j++) {
        const part = candidate.content.parts[j];
        if (part.inlineData && part.inlineData.data) {
          imageData = part.inlineData.data;
          mimeType = part.inlineData.mimeType || 'image/png';
          foundLocation = `candidates[${i}].content.parts[${j}].inlineData`;
          console.log('‚úÖ Found inline image data in candidates, MIME type:', mimeType);
          break;
        }
      }
      if (imageData) break;
    }
  }
}

// If no inline data found, try to find image URL
if (!imageData) {
  console.log('No inline data found, searching for image URL');

  let textOutput = '';
  if (inputData.output) {
    textOutput = JSON.stringify(inputData.output);
  } else if (inputData.text) {
    textOutput = String(inputData.text);
  } else {
    textOutput = JSON.stringify(inputData);
  }

  console.log('Searching text content length:', textOutput.length);

  // Search for various image URL formats
  const urlPatterns = [
    /https?:\/\/[^\s"'<>]+\.(png|jpg|jpeg|gif|webp)/gi,
    /https?:\/\/[^\s"'<>]*image[^\s"'<>]*/gi,
    /https?:\/\/[^\s"'<>]*\.googleapis\.com[^\s"'<>]*/gi
  ];

  for (const pattern of urlPatterns) {
    const matches = textOutput.match(pattern);
    if (matches && matches.length > 0) {
      console.log('‚úÖ Found image URL:', matches[0]);
      foundLocation = 'text_url_search';
      return {
        json: {
          has_image: true,
          image_type: 'url',
          image_url: matches[0],
          original_output: inputData,
          found_location: foundLocation,
          search_pattern: pattern.toString()
        }
      };
    }
  }
}

// Return result
if (imageData) {
  console.log('‚úÖ Successfully extracted image data, source:', foundLocation);
  return {
    json: {
      has_image: true,
      image_type: 'base64',
      image_data: imageData,
      mime_type: mimeType,
      original_output: inputData,
      found_location: foundLocation
    }
  };
} else {
  console.log('‚ùå No image data found');

  // Provide detailed debug information
  const debugInfo = {
    hasOutput: !!inputData.output,
    outputLength: inputData.output ? inputData.output.length : 0,
    hasCandidates: !!inputData.candidates,
    candidatesLength: inputData.candidates ? inputData.candidates.length : 0,
    hasText: !!inputData.text,
    textLength: inputData.text ? inputData.text.length : 0,
    topLevelKeys: Object.keys(inputData)
  };

  return {
    json: {
      has_image: false,
      image_data: null,
      original_output: inputData,
      debug_info: debugInfo,
      error: 'No generated image data found, AI may not have successfully generated the image'
    }
  };
}

// Unified handling for both base64 and URL
const inputData = $input.first().json;
console.log('=== Image Processing ===');
console.log('Input data:', { 
  has_image: inputData.has_image, 
  image_type: inputData.image_type,
  has_image_data: !!inputData.image_data,
  has_image_url: !!inputData.image_url,
  found_location: inputData.found_location
});

// Case 1: Process base64 format image data
if (inputData.has_image === true && inputData.image_type === 'base64' && inputData.image_data) {
  console.log('‚úÖ Processing base64 image data');

  try {
    // Process base64 data
    let base64Data = inputData.image_data;

    // Remove possible data URL prefix
    if (base64Data.startsWith('data:')) {
      base64Data = base64Data.split(',')[1];
    }

    // Generate complete data URL
    const mimeType = inputData.mime_type || 'image/png';
    const generatedImageBase64 = `data:${mimeType};base64,${base64Data}`;

    // Generate filename
    let extension = 'png';
    if (mimeType.includes('jpeg') || mimeType.includes('jpg')) {
      extension = 'jpg';
    } else if (mimeType.includes('gif')) {
      extension = 'gif';
    } else if (mimeType.includes('webp')) {
      extension = 'webp';
    }

    const fileName = `figurine_${new Date().toISOString().replace(/[:.]/g, '-')}.${extension}`;

    console.log('‚úÖ base64 processing successful');
    console.log('- Filename:', fileName);
    console.log('- MIME type:', mimeType);
    console.log('- Data size:', base64Data.length, 'characters');

    // Return final result directly, skip download process
    return {
      json: {
        success: true,
        generatedImage: generatedImageBase64,
        fileName: fileName,
        mimeType: mimeType,
        message: 'üéé Figurine model generated successfully!',
        processType: 'base64_direct',
        source: inputData.found_location,
        // Special flag to skip download node
        skipDownload: true
      }
    };

  } catch (error) {
    console.error('‚ùå Error processing base64 data:', error);
    return {
      json: {
        success: false,
        error: 'base64 data processing failed: ' + error.message,
        processType: 'base64_error',
        skipDownload: true
      }
    };
  }
}

// Case 2: Process image URL, needs download
else if (inputData.has_image === true && inputData.image_type === 'url' && inputData.image_url) {
  console.log('‚úÖ Processing image URL:', inputData.image_url);

  // Validate URL format
  try {
    new URL(inputData.image_url);

    return {
      json: {
        success: true,
        needDownload: true,
        imageUrl: inputData.image_url,
        message: 'Found image URL, preparing to download',
        processType: 'url_download',
        source: inputData.found_location,
        skipDownload: false
      }
    };
  } catch (urlError) {
    console.error('‚ùå Invalid image URL:', inputData.image_url);
    return {
      json: {
        success: false,
        error: 'Invalid image URL: ' + inputData.image_url,
        processType: 'url_error',
        skipDownload: true
      }
    };
  }
}

// Case 3: No valid image data found
else {
  console.log('‚ùå No valid image data found');

  const errorMessage = inputData.error || 'No generated image data found';

  return {
    json: {
      success: false,
      error: errorMessage,
      message: 'Image generation failed, please try again',
      processType: 'no_image',
      debug_info: inputData.debug_info || null,
      original_output: inputData.original_output || null,
      skipDownload: true
    }
  };
}
